import { Scene } from '../common/game';
import ShaderProgram from '../common/shader-program';

// In this scene we will draw one colored triangle
// The goal of this scene is to learn about:
// 1- Using a single buffer to feed multiple attributes
export default class ColoredTriangleScene2 extends Scene {
    program: ShaderProgram;
    VAO: WebGLVertexArrayObject;
    VBO: WebGLBuffer;

    public load(): void {
        // We will use the exact same shader we used in the previous scene. No changes are needed. 
        this.game.loader.load({
            ["color.vert"]:{url:'shaders/color.vert', type:'text'},
            ["color.frag"]:{url:'shaders/color.frag', type:'text'}
        });
    } 
    
    public start(): void {
        this.program = new ShaderProgram(this.gl);
        this.program.attach(this.game.loader.resources["color.vert"], this.gl.VERTEX_SHADER);
        this.program.attach(this.game.loader.resources["color.frag"], this.gl.FRAGMENT_SHADER);
        this.program.link();

        // We will put the position and color data for triangle in one buffer
        // Every 7 numbers represent 1 vertex (x,y,z,r,g,b,a)
        // We choose Float32 for both position and color since Uint8 is not suitable for positions
        const vertices = new Float32Array([
            -0.5, -0.5, 0.0, 1, 0, 0, 1,
             0.5, -0.5, 0.0, 0, 1, 0, 1,
             0.0,  0.5, 0.0, 0, 0, 1, 1,
        ]);
        // Note: we could have used a ArrayBuffer to have a mixed format (float32 for position and Uint8 for color) but I didn't do so just because it felt bothersome.

        this.VAO = this.gl.createVertexArray();
        this.VBO = this.gl.createBuffer();
    
        this.gl.bindVertexArray(this.VAO);
    
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.VBO);
        this.gl.bufferData(this.gl.ARRAY_BUFFER, vertices, this.gl.STATIC_DRAW);

        const positionAttrib = this.gl.getAttribLocation(this.program.program, "position");
        this.gl.enableVertexAttribArray(positionAttrib);
        // Note that we need to set the stride ourselves since the default value (3*4) is not suitable for our buffer (each vertex is 7*4 bytes after the previous one)
        this.gl.vertexAttribPointer(positionAttrib, 3, this.gl.FLOAT, false, 7*4, 0);

        const colorAttrib = this.gl.getAttribLocation(this.program.program, "color");
        this.gl.enableVertexAttribArray(colorAttrib);
        // We set the same stride as above (7*4) but we set the offset to 3*4 since we need to skip the first 3 floats which belong to the first vertex position
        this.gl.vertexAttribPointer(colorAttrib, 4, this.gl.FLOAT, false, 7*4, 3*4);

        // Now the VAO knows that we have 2 attributes and that both of them read from the same buffer.
        this.gl.bindVertexArray(null);

        this.gl.clearColor(0,0,0,1);
    }
    
    public draw(deltaTime: number): void {
        this.gl.clear(this.gl.COLOR_BUFFER_BIT);
        
        this.gl.useProgram(this.program.program);

        this.gl.bindVertexArray(this.VAO);
        this.gl.drawArrays(this.gl.TRIANGLES, 0, 3);
        this.gl.bindVertexArray(null);
    }
    
    public end(): void {
        this.program.dispose();
        this.program = null;
        this.gl.deleteVertexArray(this.VAO);
        this.gl.deleteBuffer(this.VBO);
    }


}
