import { Scene } from '../common/game';
import ShaderProgram from '../common/shader-program';

// In this scene we will draw one colored triangle
// The goal of this scene is to learn about:
// 1- Using multiple buffers to send extra data in our vertices
// 2- How the GPU deals with extra data
// 3- Sending data across shaders
export default class ColoredTriangleScene1 extends Scene {
    program: ShaderProgram;
    VAO: WebGLVertexArrayObject;
    // Here we have to buffers: one for vertex positions and one for vertex colors
    positionVBO: WebGLBuffer;
    colorVBO: WebGLBuffer;

    public load(): void {
        // We will use a shader that can read 2 attributes instead of one, then use the color attribute while drawing in the frame buffer
        this.game.loader.load({
            ["color.vert"]:{url:'shaders/color.vert', type:'text'},
            ["color.frag"]:{url:'shaders/color.frag', type:'text'}
        });
    } 
    
    public start(): void {
        // Create a shader program as usual
        this.program = new ShaderProgram(this.gl);
        this.program.attach(this.game.loader.resources["color.vert"], this.gl.VERTEX_SHADER);
        this.program.attach(this.game.loader.resources["color.frag"], this.gl.FRAGMENT_SHADER);
        this.program.link();

        const positions = new Float32Array([
            -0.5, -0.5, 0.0,
            0.5, -0.5, 0.0,
            0.0,  0.5, 0.0, 
        ]);

        // We will store colors in unsigned byte but we can use other formats too
        const colors = new Uint8Array([
            255,   0,   0, 255,
              0, 255,   0, 255,
              0,   0, 255, 255,
        ]);
    
        this.VAO = this.gl.createVertexArray();
        this.positionVBO = this.gl.createBuffer();
        this.colorVBO = this.gl.createBuffer();
    
        this.gl.bindVertexArray(this.VAO);
    
        // First, we bind and fill the position VBO
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.positionVBO);
        this.gl.bufferData(this.gl.ARRAY_BUFFER, positions, this.gl.STATIC_DRAW);

        const positionAttrib = this.gl.getAttribLocation(this.program.program, "position");
        this.gl.enableVertexAttribArray(positionAttrib);
        // This call knows that it will read from positionVBO since it is the one currently bound to ARRAY_BUFFER
        this.gl.vertexAttribPointer(positionAttrib, 3, this.gl.FLOAT, false, 0, 0);

        // Second, we bind and fill the color VBO (by the way, order doesn't matter, we could have setup the color VBP before the position VBO if we wanted)
        this.gl.bindBuffer(this.gl.ARRAY_BUFFER, this.colorVBO);
        this.gl.bufferData(this.gl.ARRAY_BUFFER, colors, this.gl.STATIC_DRAW);

        const colorAttrib = this.gl.getAttribLocation(this.program.program, "color");
        this.gl.enableVertexAttribArray(colorAttrib);
        // This call knows that it will read from colorVBO since it is the one currently bound to ARRAY_BUFFER
        // We will set the normalize param to true since colors in glsl are from 0 to 1 and our color are represented in unsigned byte (range from 0 to 255)
        this.gl.vertexAttribPointer(colorAttrib, 4, this.gl.UNSIGNED_BYTE, true, 0, 0);

        // Our VAO already knows that the data will be read from 2 buffer since it stored 2 vertexAttribPointer, each reading from a different buffer
        this.gl.bindVertexArray(null);

        this.gl.clearColor(0,0,0,1);
    }
    
    public draw(deltaTime: number): void {
        this.gl.clear(this.gl.COLOR_BUFFER_BIT);
        
        this.gl.useProgram(this.program.program);

        // We will draw using our VAO as usual
        this.gl.bindVertexArray(this.VAO);
        this.gl.drawArrays(this.gl.TRIANGLES, 0, 3);
        this.gl.bindVertexArray(null);
    }
    
    public end(): void {
        this.program.dispose();
        this.program = null;
        this.gl.deleteVertexArray(this.VAO);
        this.gl.deleteBuffer(this.positionVBO);
        this.gl.deleteBuffer(this.colorVBO);
    }


}
